import React, {Component}from 'react';
import Header from './components/layout/Header';
import Todos from './components/Todos';
import AddTodos from './components/AddTodo';

import './App.css';

class App extends Component {

  state = {
    todos: [
      //these are the items in the todo list
      //id is the unique id
      //title is the title of the task
      //complete is the value if the task was completed
      {
        id: 1,
        title: 'Take out the trash',
        complete: false
      },
      {
        id: 2,
        title: 'Make dinner',
        complete: true
      },
      {
        id: 3,
        title: 'Do the dishes',
        complete: false
      }
    ]
  }

  markComplete = (id) => {
    //we want to set the object in the todos array that matches the id that was passed from TodoItem.js
    this.setState({todos: this.state.todos.map(todo => {
      if(todo.id === id){
        //this will set the todo complete to the oppisite
        todo.complete = !todo.complete
      }
      return todo;
    }) });
  }
  //delTodo
  delTodo = (id) =>{
    this.setState({todos:[...this.state.todos.filter(todo => todo.id !== id)]});
  }

  render(){
    console.log(this.state.todos)
    return (
      <div className="App">
        <Header />
        <Todos todos={this.state.todos} markComplete={this.markComplete} delTodo={this.delTodo}/>   
      </div>
    );
  }
}

export default App;
